pub enum PlatformError {

    #[fail(display = "No platforms found.")]
    NoPlatforms,
}

pub enum DeviceError {

    #[fail(display = "No devices found on the specified platform.")]
    NoDevices,

    #[fail(display = "Empty device list provided.")]
    ResolveIdxsEmptyDeviceList,

    #[fail(display =
           "An index in the resolve list is out of range (index: {}, max: {})",
           idx, max)]
    ResolveIdxsInvalidIndex {
        idx: usize,
        max: usize,
    },
}

pub enum BuildOpt {
    CmplrDefine {
        ident: String,
        val: String,
    },
    CmplrInclDir {
        path: String,
    },
    CmplrOther(String),
    IncludeDefine {
        ident: String,
        val: String,
    },
    IncludeRaw(String),
    IncludeRawEof(String),
}

pub enum BufferCmdKind<'c, T> where T: 'c {
    Unspecified,
    Read,
    Write,
    Map,
    Copy {
        dst_buffer: &'c MemCore,
        dst_offset: Option<usize>,
        len: Option<usize>,
    },
    Fill {
        pattern: T,
        len: Option<usize>,
    },
    CopyToImage {
        image: &'c MemCore,
        dst_origin: [usize; ],
        region: [usize; ],
    },
    GLAcquire,
    GLRelease,
}

pub enum BufferCmdDataShape {
    Lin {
        offset: usize,
    },
    Rect {
        src_origin: [usize; ],
        dst_origin: [usize; ],
        region: [usize; ],
        src_row_pitch_bytes: usize,
        src_slc_pitch_bytes: usize,
        dst_row_pitch_bytes: usize,
        dst_slc_pitch_bytes: usize,
    },
}

pub enum ReadDst<'d, T> where T: 'd {
    Slice(&'d mut [T]),
    RwVec(RwVec<T>),
    Writer(FutureWriteGuard<Vec<T>>),
    None,
}

pub enum WriteSrc<'d, T> where T: 'd {
    Slice(&'d [T]),
    RwVec(RwVec<T>),
    Reader(FutureReadGuard<Vec<T>>),
    None,
}

pub enum ImageCmdKind<'c, T: 'c> {
    Unspecified,
    Read {
        data: &'c mut [T],
    },
    Write {
        data: &'c [T],
    },
    Map,
    Fill {
        color: T,
    },
    Copy {
        dst_image: &'c MemCore,
        dst_origin: [usize; ],
    },
    CopyToBuffer {
        buffer: &'c MemCore,
        dst_origin: usize,
    },
    GLAcquire,
    GLRelease,
}

pub enum SpatialDims {
    Unspecified,
    One(usize),
    Two(usize, usize),
    Three(usize, usize, usize),
}

pub enum ClNullEventPtrEnum<'a> {
    Null,
    Event(&'a mut Event),
    EventList(&'a mut EventList),
}
